import React, { useState } from 'react';
import { useSearchParams, Link } from 'react-router-dom';
import api from '../api';
import './styles.css'; // Ensure you have styles.css for custom styles

function Login() {
  const [searchParams] = useSearchParams();
  const role = searchParams.get('role') || 'manager'; // Default to "manager" if no role is provided
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/auth/login', { email, password, role });
      setMessage('Login successful!');

      // Redirect based on role
      if (role === 'manager') {
        window.location.href = '/manager-dashboard'; // Redirect manager to dashboard
      } else {
        window.location.href = '/attendee-dashboard'; // Redirect attendee
      }
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="login-container">
      <div className="login-header">
        <h1>Welcome to Eventify</h1>
        <p>Organize and RSVP to events effortlessly.</p>
      </div>

      <div className="login-role-switch">
        <Link to="?role=manager" className={role === 'manager' ? 'active' : ''}>
          Manager Login
        </Link>
        <Link to="?role=attendee" className={role === 'attendee' ? 'active' : ''}>
          Attendee Login
        </Link>
      </div>

      <form onSubmit={handleSubmit} className="login-form">
        <h2>Login as {role}</h2>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        {message && <p className="message">{message}</p>}
      </form>

      <div className="login-footer">
        <p>
          Don't have an account?{' '}
          <Link to={`/signup?role=${role}`}>Sign up here</Link>.
        </p>
        <p>
          If you want to browse and RSVP to events,{' '}
          <Link to="/events">click here</Link>.
        </p>
      </div>
    </div>
  );
}

export default Login;
