// client/src/components/ManagerDashboard.js
import React, { useState, useEffect } from 'react';
import api from '../api';
import jwtDecode from 'jwt-decode';
import './ManagerDashboard.css'; // Import the CSS file

function ManagerDashboard() {
  const [events, setEvents] = useState([]);
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [location, setLocation] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [user, setUser] = useState(null);

  // Fetch user information from token
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decoded = jwtDecode(token);
      setUser(decoded);
    }
  }, []);

  // Fetch all events
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get('/events');
        setEvents(response.data);
      } catch (err) {
        console.error('Error fetching events:', err);
        setError('Failed to load events. Please try again later.');
      }
    };
    fetchEvents();
  }, []);

  // Add new event
  const handleAddEvent = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/events', {
        name,
        date,
        time,
        location,
        createdBy: user.name, // Use the logged-in user's name
      });
      setMessage(response.data.message || 'Event added successfully!');
      setEvents([...events, response.data.event]); // Update event list dynamically
      setName('');
      setDate('');
      setTime('');
      setLocation('');
    } catch (err) {
      console.error('Error adding event:', err);
      setError('Failed to add event. Please check your input and try again.');
    }
  };

  // Handle message close
  const handleCloseMessage = () => {
    setMessage('');
    setError('');
  };

  return (
    <div className="manager-dashboard">
      <h1>Manager Dashboard</h1>

      {/* Success and Error Messages */}
      {message && (
        <div className="alert success" onClick={handleCloseMessage}>
          {message}
        </div>
      )}
      {error && (
        <div className="alert error" onClick={handleCloseMessage}>
          {error}
        </div>
      )}

      {/* Events List */}
      <h2>All Events</h2>
      <ul className="event-list">
        {events.length === 0 ? (
          <p>No events found. Please add an event.</p>
        ) : (
          events.map((event) => (
            <li key={event._id} className="event-item">
              <h3>{event.name}</h3>
              <p>
                <strong>Date:</strong>{' '}
                {new Date(event.date).toLocaleDateString()}
              </p>
              <p>
                <strong>Time:</strong> {event.time}
              </p>
              <p>
                <strong>Location:</strong> {event.location}
              </p>
              <p>
                <strong>Created By:</strong> {event.createdBy}
              </p>
              {event.guests && event.guests.length > 0 && (
                <>
                  <h4>Guests:</h4>
                  <ul>
                    {event.guests.map((guest, index) => (
                      <li key={index}>
                        {guest.name} ({guest.email}) - RSVP: {guest.rsvp}
                      </li>
                    ))}
                  </ul>
                </>
              )}
              {event.checklist && event.checklist.length > 0 && (
                <>
                  <h4>Checklist:</h4>
                  <ul>
                    {event.checklist.map((task, index) => (
                      <li key={index}>
                        {task.task} - {task.completed ? 'Completed' : 'Pending'}
                      </li>
                    ))}
                  </ul>
                </>
              )}
            </li>
          ))
        )}
      </ul>

      {/* Add Event Form */}
      <h2>Add Event</h2>
      <form onSubmit={handleAddEvent} className="add-event-form">
        <input
          type="text"
          placeholder="Event Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
        />
        <button type="submit" className="submit-btn">
          Add Event
        </button>
      </form>
    </div>
  );
}

export default ManagerDashboard;
