import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';
import './styles.css';

function ManagerDashboard() {
  const [events, setEvents] = useState([]);
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [location, setLocation] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  // Fetch all events
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get('/events');
        setEvents(response.data);
      } catch (err) {
        console.error('Error fetching events:', err);
        setMessage('Failed to load events');
      }
    };
    fetchEvents();
  }, []);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await api.get('/auth/check');
        setUser(response.data.user);
      } catch (err) {
        console.error('Error fetching user:', err);
        setError('Failed to load user information. Please try again later.');
      }
    };
    fetchUser();
  }, []);

  // Add new event
  const handleAddEvent = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/events', {
        name,
        date,
        time,
        location,
        createdBy: user.name, // Use the logged-in manager's name
      });
      setMessage(response.data.message);
      setEvents([...events, response.data.event]); // Update event list dynamically
      resetForm();
    } catch (err) {
      console.error('Error adding event:', err);
      setMessage('Failed to add event');
    }
  };

  // Delete an event with confirmation
  const handleDeleteEvent = async (eventId) => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      try {
        const response = await api.delete(`/events/${eventId}`);
        setMessage(response.data.message);
        setEvents(events.filter((event) => event._id !== eventId));
      } catch (err) {
        console.error('Error deleting event:', err);
        setMessage('Failed to delete event');
      }
    }
  };

  // Reset form fields after submission or cancellation
  const resetForm = () => {
    setName('');
    setDate('');
    setTime('');
    setLocation('');
  };

  return (
    <div className="manager-dashboard">
      <h1>Manager Dashboard</h1>
      {message && <p>{message}</p>}

      <h2>All Events</h2>
      <ul className="event-list">
        {events.map((event) => (
          <li key={event._id} className="event-card">
            <h3>{event.name}</h3>
            <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
            <p><strong>Time:</strong> {event.time}</p>
            <p><strong>Location:</strong> {event.location}</p>
            <p><strong>Created By:</strong> {event.createdBy}</p>

            <h4>Guests:</h4>
            <ul>
              {event.guests.map((guest, index) => (
                <li key={index}>
                  {guest.name} ({guest.email}) - RSVP: {guest.rsvp}
                </li>
              ))}
            </ul>

            <h4>Checklist:</h4>
            <ul>
              {event.checklist.map((task, index) => (
                <li key={index}>
                  {task.task} - {task.completed ? 'Completed' : 'Pending'}
                </li>
              ))}
            </ul>

            {event.createdBy === managerName && (
              <div className="event-actions">
                <button onClick={() => navigate(`/edit-event/${event._id}`)}>Edit</button>
                <button onClick={() => handleDeleteEvent(event._id)}>Delete</button>
              </div>
            )}
          </li>
        ))}
      </ul>

      <h2>Add New Event</h2>
      <form onSubmit={handleAddEvent}>
        <input
          type="text"
          placeholder="Event Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <br />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <br />
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
        <br />
        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
        />
        <br />
        <button type="submit">Add Event</button>
      </form>
    </div>
  );
}

export default ManagerDashboard;
