import React, { useState } from 'react';
import { useSearchParams, Link } from 'react-router-dom'; // Import Link for navigation
import api from '../api';

function Login() {
  const [searchParams] = useSearchParams();
  const role = searchParams.get('role'); // Get the role (manager or attendee) from the query string
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/auth/login', { email, password, role });
      setMessage('Login successful!');


      // Redirect based on role
      if (role === 'manager') {
        window.location.href = '/manager-dashboard'; // Redirect manager to dashboard
      } else {
        window.location.href = '/attendee-dashboard'; // Redirect attendee
      }
    } catch (err) {
      console.log(err);
      setMessage(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h2>Login as {role}</h2>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
        {message && <p>{message}</p>}
      </form>

      {/* Add "Don't have an account?" message */}
      <p>
        Don't have an account?{' '}
        <Link to={`/signup?role=${role}`}>Sign up here</Link>
      </p>
      <p>
        If you want to browse and RSVP to events,{' '}
        <Link to="/events">click here</Link>.
      </p>
    </div>
  );
}

export default Login;
