import React, { useState, useEffect } from 'react';
import api from '../api';
import './styles.css';
import { useNavigate } from 'react-router-dom'; // Import useNavigate


function ManagerDashboard() {
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [location, setLocation] = useState('');
  const [message, setMessage] = useState('');
  const [editEventId, setEditEventId] = useState(null); // Track event being edited
  const managerName = 'Manager'; // Replace with actual manager's name from session or JWT

  // Fetch all events
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get('/events');
        setEvents(response.data);
      } catch (err) {
        console.error('Error fetching events:', err);
        setMessage('Failed to load events');
      }
    };
    fetchEvents();
  }, []);

  // Add or Update an event
  const handleEventSubmit = async (e) => {
    e.preventDefault();

    if (editEventId) {
      // Update an existing event
      try {
        const response = await api.put(`/events/${editEventId}`, {
          name,
          date,
          time,
          location,
        });
        setMessage('Event updated successfully!');
        setEvents(events.map((event) =>
          event._id === editEventId ? { ...event, ...response.data.event } : event
        ));
        resetForm();
      } catch (err) {
        console.error('Error updating event:', err);
        setMessage('Failed to update event');
      }
    } else {
      // Add a new event
      try {
        const response = await api.post('/events', {
          name,
          date,
          time,
          location,
          createdBy: managerName,
        });
        setMessage(response.data.message);
        setEvents([...events, response.data.event]);
        resetForm();
      } catch (err) {
        console.error('Error adding event:', err);
        setMessage('Failed to add event');
      }
    }
  };

  // Delete an event with confirmation
  const handleDeleteEvent = async (eventId) => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      try {
        const response = await api.delete(`/events/${eventId}`);
        setMessage(response.data.message);
        setEvents(events.filter((event) => event._id !== eventId));
      } catch (err) {
        console.error('Error deleting event:', err);
        setMessage('Failed to delete event');
      }
    }
  };

  // Reset form after submission or cancellation
  const resetForm = () => {
    setName('');
    setDate('');
    setTime('');
    setLocation('');
    setEditEventId(null);
  };

  // Populate the form with event data for editing
  const handleEditEvent = (event) => {
    setEditEventId(event._id);
    setName(event.name);
    setDate(event.date.slice(0, 10)); // Format for input[type="date"]
    setTime(event.time);
    setLocation(event.location);
  };

 return (
    <div className="manager-dashboard">
      <h1>Manager Dashboard</h1>
      {message && <p>{message}</p>}

      <h2>All Events</h2>
      <ul className="event-list">
        {events.map((event) => (
          <li key={event._id} className="event-card">
            <h3>{event.name}</h3>
            <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
            <p><strong>Time:</strong> {event.time}</p>
            <p><strong>Location:</strong> {event.location}</p>
            <p><strong>Created By:</strong> {event.createdBy}</p>

            {event.createdBy === managerName && (
              <div className="event-actions">
                <button onClick={() => navigate(`/edit-event/${event._id}`)}>Edit</button>
                <button onClick={() => handleDeleteEvent(event._id)}>Delete</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ManagerDashboard;
