// client/src/components/EventDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api';
import './EventDetails.css';

function EventDetails() {
  const { id } = useParams();
  const [event, setEvent] = useState(null);
  const [error, setError] = useState('');
  const [rsvpName, setRsvpName] = useState('');
  const [rsvpEmail, setRsvpEmail] = useState('');
  const [rsvpStatus, setRsvpStatus] = useState('');

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const response = await api.get(`/events/${id}`);
        setEvent(response.data);
      } catch (err) {
        console.error('Error fetching event:', err);
        setError('Failed to load event details. Please try again later.');
      }
    };
    fetchEvent();
  }, [id]);

  const handleRsvpSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post(`/events/${id}/rsvp`, {
        name: rsvpName,
        email: rsvpEmail,
        rsvp: rsvpStatus,
      });
      setEvent(response.data.event);
      setRsvpName('');
      setRsvpEmail('');
      setRsvpStatus('');
      alert('RSVP submitted successfully!');
    } catch (err) {
      console.error('Error submitting RSVP:', err);
      setError('Failed to submit RSVP. Please try again later.');
    }
  };

  if (error) {
    return <p>{error}</p>;
  }

  if (!event) {
    return <p>Loading...</p>;
  }

  return (
    <div className="event-details">
      <h1>{event.name}</h1>
      <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
      <p><strong>Time:</strong> {event.time}</p>
      <p><strong>Location:</strong> {event.location}</p>
      <p><strong>Created By:</strong> {event.createdBy}</p>
      <h2>Guests</h2>
      <ul>
        {event.guests.map((guest, index) => (
          <li key={index}>
            {guest.name} ({guest.email}) - RSVP: {guest.rsvp}
          </li>
        ))}
      </ul>
      <h2>Checklist</h2>
      <ul>
        {event.checklist.map((task, index) => (
          <li key={index}>
            {task.task} - {task.completed ? 'Completed' : 'Pending'}
          </li>
        ))}
      </ul>

      <h2>RSVP</h2>
      <form onSubmit={handleRsvpSubmit} className="rsvp-form">
        <input
          type="text"
          placeholder="Your Name"
          value={rsvpName}
          onChange={(e) => setRsvpName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Your Email"
          value={rsvpEmail}
          onChange={(e) => setRsvpEmail(e.target.value)}
          required
        />
        <select
          value={rsvpStatus}
          onChange={(e) => setRsvpStatus(e.target.value)}
          required
        >
          <option value="">Select RSVP Status</option>
          <option value="Yes">Yes</option>
          <option value="No">No</option>
          <option value="Maybe">Maybe</option>
        </select>
        <button type="submit">Submit RSVP</button>
      </form>
    </div>
  );
}

export default EventDetails;
