import React, { useState, useEffect } from 'react';
import api from '../api';

function ManagerDashboard() {
  const [events, setEvents] = useState([]);
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [location, setLocation] = useState('');
  const [message, setMessage] = useState('');

  // Fetch all events
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get('/events');
        setEvents(response.data);
      } catch (err) {
        console.error('Error fetching events:', err);
        setMessage('Failed to load events');
      }
    };
    fetchEvents();
  }, []);

  // Add new event
  const handleAddEvent = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/events', {
        name,
        date,
        time,
        location,
        createdBy: 'Manager', // Replace with actual manager name from session or JWT
      });
      setMessage(response.data.message);
      setEvents([...events, response.data.event]); // Update event list dynamically
      setName('');
      setDate('');
      setTime('');
      setLocation('');
    } catch (err) {
      console.error('Error adding event:', err);
      setMessage('Failed to add event');
    }
  };

  return (
    <div>
      <h1>Manager Dashboard</h1>
      {message && <p>{message}</p>}

      <h2>All Events</h2>
      <ul>
        {events.map((event) => (
          <li key={event._id}>
            <h3>{event.name}</h3>
            <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
            <p><strong>Time:</strong> {event.time}</p>
            <p><strong>Location:</strong> {event.location}</p>
            <p><strong>Created By:</strong> {event.createdBy}</p>
            <h4>Guests:</h4>
            <ul>
              {event.guests.map((guest, index) => (
                <li key={index}>
                  {guest.name} ({guest.email}) - RSVP: {guest.rsvp}
                </li>
              ))}
            </ul>
            <h4>Checklist:</h4>
            <ul>
              {event.checklist.map((task, index) => (
                <li key={index}>
                  {task.task} - {task.completed ? 'Completed' : 'Pending'}
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>

      <h2>Add Event</h2>
      <form onSubmit={handleAddEvent}>
        <input
          type="text"
          placeholder="Event Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <br />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <br />
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
        <br />
        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
        />
        <br />
        <button type="submit">Add Event</button>
      </form>
    </div>
  );
}

export default ManagerDashboard;
