// client/src/components/ManagerDashboard.js
import React, { useState, useEffect } from 'react';
import api from '../api';
import './ManagerDashboard.css'; // Import the CSS file

function ManagerDashboard() {
  const [events, setEvents] = useState([]);
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [location, setLocation] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [user, setUser] = useState(null);

  // Fetch user information
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await api.get('/auth/check');
        setUser(response.data.user);
      } catch (err) {
        console.error('Error fetching user:', err);
        setError('Failed to load user information. Please try again later.');
      }
    };
    fetchUser();
  }, []);

  // Fetch all events
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get('/events');
        setEvents(response.data);
      } catch (err) {
        console.error('Error fetching events:', err);
        setError('Failed to load events. Please try again later.');
      }
    };
    fetchEvents();
  }, []);

  // Add new event
  const handleAddEvent = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/events', {
        name,
        date,
        time,
        location,
        createdBy: user.name, // Use the logged-in user's name
      });
      setMessage(response.data.message);
      setEvents([...events, response.data.event]); // Update event list dynamically
      setName('');
      setDate('');
      setTime('');
      setLocation('');
    } catch (err) {
      console.error('Error adding event:', err);
      setMessage('Failed to add event');
    }
  };

  return (
    <div className="manager-dashboard">
      <h1>Manager Dashboard</h1>
      {message && <p>{message}</p>}
      {error && <p className="error">{error}</p>}

      <h2>All Events</h2>
      <ul>
        {events.map((event) => (
          <li key={event._id}>
            <h3>{event.name}</h3>
            <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
            <p><strong>Time:</strong> {event.time}</p>
            <p><strong>Location:</strong> {event.location}</p>
            <p><strong>Created By:</strong> {event.createdBy}</p>
            <h4>Guests:</h4>
            <ul>
              {event.guests.map((guest, index) => (
                <li key={index}>
                  {guest.name} ({guest.email}) - RSVP: {guest.rsvp}
                </li>
              ))}
            </ul>
            <h4>Checklist:</h4>
            <ul>
              {event.checklist.map((task, index) => (
                <li key={index}>
                  {task.task} - {task.completed ? 'Completed' : 'Pending'}
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>

      <h2>Add Event</h2>
      <form onSubmit={handleAddEvent}>
        <input
          type="text"
          placeholder="Event Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
        />
        <button type="submit">Add Event</button>
      </form>
    </div>
  );
}

export default ManagerDashboard;
