import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './styles.css'; // Import the CSS file

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Simulate login API call (replace with actual API integration)
      console.log('Logging in with:', { email, password });
      setMessage('Login successful! Redirecting...');
      window.location.href = '/manager-dashboard'; // Redirect to Manager Dashboard after login
    } catch (err) {
      console.error(err);
      setMessage('Login failed. Please try again.');
    }
  };

  return (
    <div className="container">
      <h1>Welcome to Event Planner</h1>
      <p>Please log in to continue:</p>
      <form onSubmit={handleSubmit} className="login-form">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login as Manager</button>
        {message && <p>{message}</p>}
      </form>

      <p>
        Don't have an account?{' '}
        <Link to="/signup?role=manager">Sign up here</Link>
      </p>

      <p>
        If you want to browse and RSVP to events,{' '}
        <Link to="/events">click here</Link>.
      </p>
    </div>
  );
}

export default Login;
