import React, { useState, useEffect } from 'react';
import api from '../api';
import './styles.css';

function ManagerDashboard() {
  const [events, setEvents] = useState([]);
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [location, setLocation] = useState('');
  const [message, setMessage] = useState('');
  const managerName = 'Manager'; // Replace with actual manager's name from session or JWT

  // Fetch all events
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get('/events');
        setEvents(response.data);
      } catch (err) {
        console.error('Error fetching events:', err);
        setMessage('Failed to load events');
      }
    };
    fetchEvents();
  }, []);

  // Add new event
  const handleAddEvent = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/events', {
        name,
        date,
        time,
        location,
        createdBy: managerName, // Use the logged-in manager's name
      });
      setMessage(response.data.message);
      setEvents([...events, response.data.event]);
      setName('');
      setDate('');
      setTime('');
      setLocation('');
    } catch (err) {
      console.error('Error adding event:', err);
      setMessage('Failed to add event');
    }
  };

  // Delete an event
  const handleDeleteEvent = async (eventId) => {
    try {
      const response = await api.delete(`/events/${eventId}`);
      setMessage(response.data.message);
      setEvents(events.filter((event) => event._id !== eventId));
    } catch (err) {
      console.error('Error deleting event:', err);
      setMessage('Failed to delete event');
    }
  };

  // Mark a checklist task as completed
  const handleToggleTask = async (eventId, taskIndex) => {
    try {
      const event = events.find((event) => event._id === eventId);
      const updatedChecklist = [...event.checklist];
      updatedChecklist[taskIndex].completed = !updatedChecklist[taskIndex].completed;

      const response = await api.put(`/events/${eventId}`, { checklist: updatedChecklist });
      setMessage('Task status updated successfully');
      setEvents(
        events.map((ev) =>
          ev._id === eventId ? { ...ev, checklist: response.data.event.checklist } : ev
        )
      );
    } catch (err) {
      console.error('Error updating task:', err);
      setMessage('Failed to update task');
    }
  };

  return (
    <div className="manager-dashboard">
      <h1>Manager Dashboard</h1>
      {message && <p>{message}</p>}

      <h2>Dashboard Summary</h2>
      <p><strong>Total Events:</strong> {events.length}</p>
      <p>
        <strong>Total RSVPs:</strong>{' '}
        {events.reduce((total, event) => total + (event.guests?.length || 0), 0)}
      </p>

      <h2>All Events</h2>
      <ul className="event-list">
        {events.map((event) => (
          <li key={event._id} className="event-card">
            <h3>{event.name}</h3>
            <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
            <p><strong>Time:</strong> {event.time}</p>
            <p><strong>Location:</strong> {event.location}</p>
            <p><strong>Created By:</strong> {event.createdBy}</p>

            <h4>Guests:</h4>
            <ul>
              {event.guests.map((guest, index) => (
                <li key={index}>
                  {guest.name} ({guest.email}) - RSVP: {guest.rsvp}
                </li>
              ))}
            </ul>

            <h4>Checklist:</h4>
            <ul>
              {event.checklist.map((task, index) => (
                <li key={index}>
                  {task.task} - {task.completed ? 'Completed' : 'Pending'}
                  {event.createdBy === managerName && (
                    <button onClick={() => handleToggleTask(event._id, index)}>
                      Mark as {task.completed ? 'Pending' : 'Completed'}
                    </button>
                  )}
                </li>
              ))}
            </ul>

            {event.createdBy === managerName && (
              <div className="event-actions">
                <button onClick={() => handleDeleteEvent(event._id)}>Delete Event</button>
              </div>
            )}
          </li>
        ))}
      </ul>

      <h2>Add Event</h2>
      <form onSubmit={handleAddEvent}>
        <input
          type="text"
          placeholder="Event Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <br />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <br />
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
        <br />
        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
        />
        <br />
        <button type="submit">Add Event</button>
      </form>
    </div>
  );
}

export default ManagerDashboard;
